// Parse input - passthrough mode
const raw = $input.first().json;
let mcpUrl, toolName, args;
try {
  const p = JSON.parse(raw.query || '{}');
  mcpUrl = p.mcp_url || p.url || '';
  toolName = p.tool_name || p.tool || '';
  args = p.arguments || p.args || {};
  if (typeof args === 'string') args = JSON.parse(args);
} catch(e) {
  mcpUrl = raw.mcp_url || '';
  toolName = raw.tool_name || '';
  args = raw.arguments || raw.args || {};
  if (typeof args === 'string') try { args = JSON.parse(args); } catch(e2) {}
}

if (!mcpUrl || !toolName) {
  return [{ json: { error: `Missing mcp_url or tool_name. Got: mcp_url=${mcpUrl}, tool_name=${toolName}` } }];
}

try {
  // 1. Initialize â€” returnFullResponse to get headers
  const init = await this.helpers.httpRequest({
    method: 'POST',
    url: mcpUrl,
    headers: { 'Content-Type': 'application/json', 'Accept': 'application/json, text/event-stream' },
    body: JSON.stringify({ jsonrpc: '2.0', id: 1, method: 'initialize', params: { protocolVersion: '2024-11-05', capabilities: {}, clientInfo: { name: 'greg-mcp', version: '1.0' } } }),
    returnFullResponse: true,
    encoding: 'utf-8',
    json: false
  });

  const sid = init.headers['mcp-session-id'];
  if (!sid) return [{ json: { error: 'No MCP session ID', body: String(init.body).substring(0,200) } }];

  // 2. Initialized notification
  await this.helpers.httpRequest({
    method: 'POST',
    url: mcpUrl,
    headers: { 'Content-Type': 'application/json', 'Accept': 'application/json, text/event-stream', 'mcp-session-id': sid },
    body: JSON.stringify({ jsonrpc: '2.0', method: 'notifications/initialized' }),
    json: false
  });

  // 3. Call tool
  const resp = await this.helpers.httpRequest({
    method: 'POST',
    url: mcpUrl,
    headers: { 'Content-Type': 'application/json', 'Accept': 'application/json, text/event-stream', 'mcp-session-id': sid },
    body: JSON.stringify({ jsonrpc: '2.0', id: 2, method: 'tools/call', params: { name: toolName, arguments: args } }),
    json: false
  });

  // Parse SSE response
  const bodyStr = String(resp);
  const dataLine = bodyStr.split('\n').find(l => l.startsWith('data: '));
  if (!dataLine) return [{ json: { error: 'No data in MCP response', raw: bodyStr.substring(0,300) } }];
  const result = JSON.parse(dataLine.substring(6));
  const text = result.result?.content?.[0]?.text || JSON.stringify(result.result || result);

  return [{ json: { response: text, tool: toolName, mcp_url: mcpUrl } }];
} catch(e) {
  return [{ json: { error: e.message, stack: e.stack?.substring(0,200), tool: toolName, mcp_url: mcpUrl } }];
}
