const msg = $input.first().json.message;
const chatId = String(msg.chat.id);
const text = msg.text || '';

// Check if setup already done
const result = await helpers.httpRequest({
  method: 'GET',
  url: '{{SUPABASE_URL}}/rest/v1/user_profiles?user_id=eq.telegram:' + chatId + '&select=setup_done',
  headers: {
    'apikey': '{{SUPABASE_SERVICE_KEY}}',
    'Authorization': 'Bearer {{SUPABASE_SERVICE_KEY}}'
  }
});

const profile = Array.isArray(result) ? result[0] : null;
const setupDone = profile?.setup_done === true;

return [{json: {chatId, text, setupDone, profile}}];

const {chatId, text, profile} = $input.first().json;
const step = profile?.setup_step || 0;

const SUPA_URL = '{{SUPABASE_URL}}/rest/v1';
const SUPA_KEY = '{{SUPABASE_SERVICE_KEY}}';
const sHead = {'apikey': SUPA_KEY, 'Authorization': 'Bearer ' + SUPA_KEY, 'Content-Type': 'application/json', 'Prefer': 'return=minimal'};

let reply = '';
let nextStep = step;

if (step === 0 || text === '/start') {
  reply = `ðŸ‘‹ Willkommen! Ich bin dein neuer KI-Assistent.\n\nLass uns kurz einrichten wer ich bin und wer du bist â€” dauert nur 2 Minuten.\n\n*Schritt 1/4:* Wie soll ich heiÃŸen?\n_(z.B. "Alex", "Kai", "Assistant")_`;
  nextStep = 1;
} else if (step === 1) {
  const botName = text.trim();
  await helpers.httpRequest({method:'POST', url:SUPA_URL+'/soul?on_conflict=key', headers:sHead,
    body:JSON.stringify({key:'name', content: botName})});
  await helpers.httpRequest({method:'POST', url:SUPA_URL+'/soul?on_conflict=key', headers:sHead,
    body:JSON.stringify({key:'persona', content:`Du bist ${botName}, ein hilfreicher KI-Assistent. Sprich locker und direkt. Keine Floskeln. Kurz, klar. Emojis sparsam.`})});
  reply = `Super, ich bin jetzt *${botName}*! ðŸŽ‰\n\n*Schritt 2/4:* Wie heiÃŸt du?`;
  nextStep = 2;
} else if (step === 2) {
  const userName = text.trim();
  await helpers.httpRequest({method:'POST', url:SUPA_URL+'/user_profiles?on_conflict=user_id', headers:{...sHead,'Prefer':'return=minimal'},
    body:JSON.stringify({user_id:'telegram:'+chatId, name: userName.toLowerCase().replace(/\s+/g,'_'), display_name: userName})});
  reply = `Hallo *${userName}*! ðŸ‘‹\n\n*Schritt 3/4:* In welcher Sprache soll ich hauptsÃ¤chlich antworten?\n_(z.B. "Deutsch", "English", "FranÃ§ais")_`;
  nextStep = 3;
} else if (step === 3) {
  const lang = text.trim();
  await helpers.httpRequest({method:'POST', url:SUPA_URL+'/soul?on_conflict=key', headers:sHead,
    body:JSON.stringify({key:'language', content: `Bevorzugte Sprache: ${lang}. Antworte immer auf ${lang} auÃŸer der User schreibt in einer anderen Sprache.`})});
  reply = `Alles klar, ich antworte auf *${lang}*!\n\n*Schritt 4/4:* Kurz beschreiben wofÃ¼r du mich nutzen mÃ¶chtest:\n_(z.B. "ProduktivitÃ¤t & Automatisierung", "Coding-Assistent", "PersÃ¶nlicher Assistent")_`;
  nextStep = 4;
} else if (step === 4) {
  const context = text.trim();
  await helpers.httpRequest({method:'PATCH', url:SUPA_URL+'/user_profiles?user_id=eq.telegram:'+chatId, headers:sHead,
    body:JSON.stringify({context, setup_done: true, setup_step: 5})});
  reply = `âœ… *Setup abgeschlossen!*\n\nIch bin bereit! Schreib mir einfach was du brauchst.\n\nTipp: Ich kann auch neue Tools bauen â€” sag einfach "Bau mir einen MCP Server fÃ¼r [API-Name]"`;
  nextStep = 5;
}

// Save step progress
if (nextStep > 0 && nextStep < 5) {
  await helpers.httpRequest({method:'POST', url:SUPA_URL+'/user_profiles?on_conflict=user_id', headers:{...sHead,'Prefer':'return=minimal'},
    body:JSON.stringify({user_id:'telegram:'+chatId, setup_step: nextStep, setup_done: false})});
}

return [{json: {chatId, reply, done: nextStep === 5}}];
